import { useEffect } from "react";
import { AlertTriangle, CheckCircle2, Users } from "lucide-react";
import { Chart } from "chart.js/auto";

export default function MainInfo() {
  useEffect(() => {
    let symptomChartInstance;

    const createSymptomChart = () => {
      const canvas = document.getElementById("symptomChart");
      if (!canvas) return;

      const ctx = canvas.getContext("2d");

      symptomChartInstance = new Chart(ctx, {
        type: "bar",
        data: {
          labels: ["Fever", "Cough", "Headache", "Fatigue", "Diarrhea",],
          datasets: [
            {
              label: "Cases",
              data: [368, 275, 165, 330, 137],
              backgroundColor: [
                "#60A5FA",
                "#4ADE80",
                "#A78BFA",
                "#FBBF24",
                "#F87171",
              ],
              borderRadius: 4,
              barThickness: 30,
            },
          ],
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          plugins: {
            legend: { display: false },
            tooltip: {
              callbacks: {
                title: () => "",
              },
            },
          },
          scales: {
            y: { beginAtZero: true },
            x: { grid: { display: false } },
          },
        },
      });
    };

    createSymptomChart();

    return () => {
      if (symptomChartInstance) {
        symptomChartInstance.destroy(); // cleanup on unmount
      }
    };
  }, []);

  return (
    <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
      {/* Live Symptom Tracker */}
      <div className="lg:col-span-2 bg-white p-6 rounded-lg shadow-sm">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-semibold text-gray-800">
            Live Symptom Tracker (24h)
          </h3>
          <div className="flex items-center space-x-2">
            <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
            <span className="text-sm text-gray-600">Live Updates</span>
          </div>
        </div>

        {/* Chart container */}
        <div className="h-80">
          <canvas id="symptomChart"></canvas>
        </div>
      </div>

      {/* Recent Activity */}
      <div className="bg-white p-6 rounded-lg shadow-sm">
        <h3 className="text-lg font-semibold text-gray-800 mb-4">
          Recent Activity
        </h3>


      </div>
    </div>
  );
}
