import React, { useState } from 'react';
import {
  UserHeart,
  LogOut,
  Calendar,
  Heart,
  Activity,
  Clock,
  CheckCircle,
  Pill,
  ArrowLeft,
  CalendarCheck,
  Thermometer,
  Stethoscope,
  Microscope,
  CalendarPlus,
  Check,
  AlertCircle
} from 'lucide-react';

// --- Mock Data ---
const MOCK_APPOINTMENTS = {
  ongoing: [
