const ALL_APPOINTMENTS = [...MOCK_APPOINTMENTS.ongoing, ...MOCK_APPOINTMENTS.pending, ...MOCK_APPOINTMENTS.completed];

const PatientPortal = () => {
  const [view, setView] = useState('dashboard'); // 'dashboard' or 'details'
  const [selectedAppointment, setSelectedAppointment] = useState(null);
  const [appointments, setAppointments] = useState(ALL_APPOINTMENTS);
  const [title, setTitle] = useState('Recent Appointments');

  const handleViewDetails = (appointment) => {
    setSelectedAppointment(appointment);
    setView('details');
  };

  const handleBackToDashboard = () => {
    setSelectedAppointment(null);
    setView('dashboard');
  };

  const handleFilter = (filterType, title) => {
    setTitle(title);
    switch (filterType) {
      case 'ongoing': setAppointments(MOCK_APPOINTMENTS.ongoing); break;
      case 'pending': setAppointments(MOCK_APPOINTMENTS.pending); break;
      case 'completed': setAppointments(MOCK_APPOINTMENTS.completed); break;
      case 'prescriptions': setAppointments(ALL_APPOINTMENTS.filter(a => a.medicines.some(m => m.status === 'Active'))); break;
      default: setAppointments(ALL_APPOINTMENTS);
    }
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      <Header />
      <main className="flex-1 overflow-y-auto p-6 md:p-8">
        <div className="max-w-7xl mx-auto">
          {view === 'dashboard' ? (
            <DashboardView
              appointments={appointments}
              title={title}
              onFilter={handleFilter}
              onViewDetails={handleViewDetails}
            />
          ) : (
            <AppointmentDetailsView
              appointment={selectedAppointment}
              onBack={handleBackToDashboard}
            />
          )}
        </div>
      </main>
    </div>
  );
};

