import React from "react";

 const handleStartEncounter = (patientId) => {
    console.log("Starting encounter for:", patientId);
    // You can navigate to encounter page or open modal here
  };
  const patients = [
    { id: "AC-12345", name: "Amit Singh", time: "10:15 AM" },
    { id: "AC-54321", name: "Priya Das", time: "9:58 AM" },
    { id: "AC-67890", name: "Sunita Murmu", time: "9:42 AM" },
     { id: "AC-54321", name: "Priya Das", time: "9:58 AM" },
    { id: "AC-67890", name: "Sunita Murmu", time: "9:42 AM" },
  ];

const WaitingQueue = () => {
  return (
    <div className="bg-white p-6 rounded-lg shadow">
      <h3 className="text-lg font-semibold mb-4">Waiting for Encounter</h3>
      <ul className="divide-y divide-gray-200">
        {patients.map((patient) => (
          <li
            key={patient.id}
            className="py-3 flex justify-between items-center"
          >
            <div>
              <p className="text-sm font-semibold">{patient.name}</p>
              <p className="text-xs text-gray-500">
                ID: {patient.id} â€¢ Registered at {patient.time}
              </p>
            </div>
            <button
              className="start-encounter-from-queue-btn bg-teal-500 text-white text-xs font-semibold px-3 py-1 rounded-full hover:bg-teal-600"
              onClick={() => handleStartEncounter(patient.id)}
            >
              Start Encounter
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default WaitingQueue;
