import React, { useState } from "react";
import { ArrowLeft, Image } from "lucide-react";
import Header from "../comman/Header";
import { Outlet, useNavigate } from "react-router-dom";

const RegistrationForm = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    mobile: "",
    fullName: "",
    aadhaar: "",
    state: "",
    city: "",
    district: "",
  });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleMobileSubmit = (e) => {
    e.preventDefault();
    // 🔍 check in DB if user exists (dummy logic for now)
    const userExists = false; // replace with real API check
    if (!userExists) {
      setStep(2);
    } else {
      alert("User already exists, redirecting...");
    }
  };

  const handleFinalSubmit = (e) => {
    e.preventDefault();
    console.log("Form Data:", formData);
    alert("Migrant registered successfully ✅");
  };

  return (
    <div className=" w-full ">
        <Header></Header>
        <div className=" w-full bg-white p-8 rounded-lg shadow max-w-2xl mx-auto">
        <button className="back-btn flex items-center text-sm font-semibold text-gray-600 hover:text-black mb-4" onClick={()=> navigate(-1)}>

            <ArrowLeft className="h-4 w-4 mr-2" /> Back to Dashboard
        </button>

        <h2 className="text-xl font-bold text-gray-800 mb-6 border-b pb-4">
            New Migrant Registration
        </h2>

        {step === 1 && (
            <form onSubmit={handleMobileSubmit}>
            <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                Mobile Number
                </label>
                <input
                type="tel"
                name="mobile"
                value={formData.mobile}
                onChange={handleChange}
                className="w-full p-2 border rounded-md focus:ring-2 focus:ring-purple-500"
                placeholder="Enter 10-digit number"
                required
                />
            </div>
            <div className="mt-8 text-right">
                <button
                type="submit"
                className="bg-purple-600 text-white font-semibold px-6 py-2 rounded-md hover:bg-purple-700"
                >
                Next
                </button>
            </div>
            </form>
        )}

        {step === 2 && (
            <form onSubmit={handleFinalSubmit}>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                    Full Name
                </label>
                <input
                    type="text"
                    name="fullName"
                    value={formData.fullName}
                    onChange={handleChange}
                    className="w-full p-2 border rounded-md focus:ring-2 focus:ring-purple-500"
                    placeholder="Enter full name"
                    required
                />
                </div>

                <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                    Aadhaar Number
                </label>
                <input
                    type="text"
                    name="aadhaar"
                    value={formData.aadhaar}
                    onChange={handleChange}
                    className="w-full p-2 border rounded-md focus:ring-2 focus:ring-purple-500"
                    placeholder="Enter Aadhaar number"
                    required
                />
                </div>

                <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                    State
                </label>
                <input
                    type="text"
                    name="state"
                    value={formData.state}
                    onChange={handleChange}
                    className="w-full p-2 border rounded-md focus:ring-2 focus:ring-purple-500"
                    placeholder="Enter state"
                />
                </div>

                <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                    City
                </label>
                <input
                    type="text"
                    name="city"
                    value={formData.city}
                    onChange={handleChange}
                    className="w-full p-2 border rounded-md focus:ring-2 focus:ring-purple-500"
                    placeholder="Enter city"
                />
                </div>

                <div className="md:col-span-2">
                <label className="block text-sm font-medium text-gray-700 mb-1">
                    District
                </label>
                <input
                    type="text"
                    name="district"
                    value={formData.district}
                    onChange={handleChange}
                    className="w-full p-2 border rounded-md focus:ring-2 focus:ring-purple-500"
                    placeholder="Enter district"
                />
                </div>

                <div className="md:col-span-2">
                <label className="block text-sm font-medium text-gray-700 mb-1">
                    Aadhaar Card Upload
                </label>
                <div className="mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 border-dashed rounded-md">
                    <div className="space-y-1 text-center">
                    <Image className="mx-auto h-12 w-12 text-gray-400" />
                    <div className="flex text-sm text-gray-600">
                        <label className="relative cursor-pointer bg-white rounded-md font-medium text-purple-600 hover:text-purple-500">
                        <span>Upload a file</span>
                        <input type="file" className="sr-only" />
                        </label>
                        <p className="pl-1">or use camera</p>
                    </div>
                    <p className="text-xs text-gray-500">PNG, JPG up to 10MB</p>
                    </div>
                </div>
                </div>
            </div>

            <div className="mt-8 text-right">
                <button
                type="submit"
                className="bg-purple-600 text-white font-semibold px-6 py-2 rounded-md hover:bg-purple-700"
                >
                Register Migrant
                </button>
            </div>
            </form>
        )}
        </div>
    </div>
  );
};

export default RegistrationForm;
