import React, { useEffect, useState } from "react";
import { Eye, EyeOff, HeartPulse, RefreshCw } from "lucide-react";
import Header from "./Header";

const LoginCard = () => {
  const [step, setStep] = useState(1);
  const [showPassword, setShowPassword] = useState(false);
  const [otp, setOtp] = useState(Array(6).fill(""));
  const [captcha, setCaptcha] = useState("");
  const [captchaInput, setCaptchaInput] = useState("");

  // --- Captcha Logic ---
  const generateCaptcha = () => {
    const chars = "ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";
    let result = "";
    for (let i = 0; i < 6; i++) {
      result += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    setCaptcha(result);
  };

  useEffect(() => {
    generateCaptcha();
  }, []);

  // --- OTP Input Logic ---
  const handleOtpChange = (value, index) => {
    if (/^[0-9]?$/.test(value)) {
      const newOtp = [...otp];
      newOtp[index] = value;
      setOtp(newOtp);

      // Auto move focus
      if (value && index < otp.length - 1) {
        document.getElementById(`otp-${index + 1}`).focus();
      }
    }
  };

  const handleOtpKeyDown = (e, index) => {
    if (e.key === "Backspace" && !otp[index] && index > 0) {
      document.getElementById(`otp-${index - 1}`).focus();
    }
  };

  // --- Step 1 Submit ---
  const handleCredentialsSubmit = (e) => {
    e.preventDefault();
    setStep(2);
  };

  // --- Step 2 Submit ---
  const handleOtpSubmit = (e) => {
    e.preventDefault();
    if (captchaInput !== captcha) {
      alert("Captcha mismatch. Try again!");
      generateCaptcha();
      setCaptchaInput("");
      return;
    }
    alert(`Logged in with OTP: ${otp.join("")}`);
  };

  return (
    <>
    <div className="w-full flex ">
      <Header></Header>
      <div className="bg-white p-8 rounded-xl shadow-lg max-w-md w-full">
        <div className="text-center mb-8">
          <a href="/" className="flex items-center justify-center space-x-3">
            <HeartPulse className="h-12 w-12 text-blue-600" />
            <div>
              <h1 className="text-3xl font-bold text-gray-800">
                Arogya Connect
              </h1>
              <p className="text-xs text-gray-500">Your Health, Connected</p>
            </div>
          </a>
        </div>

        {/* Step 1 - Credentials */}
        {step === 1 && (
          <div>
            <h2 className="text-2xl font-bold text-gray-800 text-center">
              Patient Login
            </h2>
            <p className="text-center text-gray-500 text-sm mt-2 mb-6">
              Welcome back! Please sign in to your account.
            </p>
            <form onSubmit={handleCredentialsSubmit}>
              <div className="mb-4">
                <label
                  htmlFor="mobile-pass"
                  className="block text-sm font-medium text-gray-700 mb-1"
                >
                  Mobile Number or ID
                </label>
                <input
                  type="text"
                  id="mobile-pass"
                  className="w-full px-4 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                  required
                />
              </div>
              <div className="mb-6">
                <div className="flex justify-between items-center">
                  <label
                    htmlFor="password"
                    className="block text-sm font-medium text-gray-700 mb-1"
                  >
                    Password
                  </label>
                  <a href="#" className="text-xs text-blue-600 hover:underline">
                    Forgot Password?
                  </a>
                </div>
                <div className="relative">
                  <input
                    type={showPassword ? "text" : "password"}
                    id="password"
                    className="w-full px-4 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                    required
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute inset-y-0 right-0 px-3 flex items-center text-gray-500"
                  >
                    {showPassword ? (
                      <EyeOff className="h-5 w-5" />
                    ) : (
                      <Eye className="h-5 w-5" />
                    )}
                  </button>
                </div>
              </div>
              <button
                type="submit"
                className="w-full bg-blue-600 text-white font-semibold py-3 rounded-md hover:bg-blue-700 transition-colors"
              >
                Proceed to OTP Verification
              </button>
            </form>
          </div>
        )}

        {/* Step 2 - OTP + Captcha */}
        {step === 2 && (
          <div>
            <h2 className="text-2xl font-bold text-gray-800 text-center">
              Two-Step Verification
            </h2>
            <p className="text-center text-gray-500 text-sm mt-2 mb-6">
              An OTP has been sent to your registered mobile number. Please
              enter it below.
            </p>
            <form onSubmit={handleOtpSubmit}>
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Enter OTP
                </label>
                <div className="flex justify-center space-x-2">
                  {otp.map((digit, index) => (
                    <input
                      key={index}
                      id={`otp-${index}`}
                      type="text"
                      maxLength="1"
                      value={digit}
                      onChange={(e) => handleOtpChange(e.target.value, index)}
                      onKeyDown={(e) => handleOtpKeyDown(e, index)}
                      className="w-12 h-14 text-center text-2xl font-semibold border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                    />
                  ))}
                </div>
              </div>

              {/* Captcha */}
              <div className="mb-6">
                <label
                  htmlFor="captcha-input"
                  className="block text-sm font-medium text-gray-700 mb-1"
                >
                  Enter Captcha
                </label>
                <div className="flex items-center space-x-2 p-2 bg-gray-100 rounded-md border">
                  <span
                    className="flex-grow select-none text-gray-700 font-mono tracking-widest text-lg font-bold line-through"
                    style={{ transform: "skew(-10deg)" }}
                  >
                    {captcha}
                  </span>
                  <button
                    type="button"
                    onClick={generateCaptcha}
                    className="p-1 text-gray-600 hover:text-blue-600"
                  >
                    <RefreshCw className="h-5 w-5" />
                  </button>
                </div>
                <input
                  type="text"
                  id="captcha-input"
                  value={captchaInput}
                  onChange={(e) => setCaptchaInput(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-md mt-2"
                  required
                />
              </div>

              <button
                type="submit"
                className="w-full bg-blue-600 text-white font-semibold py-3 rounded-md hover:bg-blue-700 transition-colors"
              >
                Verify & Login
              </button>
            </form>
          </div>
        )}

        {/* Footer */}
        <div className="mt-6 pt-6 border-t text-center text-sm">
          <p className="text-gray-500">
            Don&apos;t have an account?{" "}
            <a
              href="/register"
              className="font-semibold text-blue-600 hover:underline"
            >
              Register Now
            </a>
          </p>
        </div>
      </div>
    </div>
    </>
  );
};

export default LoginCard;
