import React, { useEffect, useState } from "react";
import { Check, Eye, EyeOff, HeartPulse } from "lucide-react";
import Header from "./Header";
import { Link } from "react-router-dom";

const RegisterCard = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [showPassword, setShowPassword] = useState(false);
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [motp, setMotp] = useState(Array(6).fill(""));
  const [adrotp, setAdrotp] = useState(Array(6).fill(""));
  const [error, setError] = useState("");
const [selectedState, setSelectedState] = useState("West Bengal");
  const [form, setForm] = useState({
    fullName:'',
    age:'',
    email:'',
    village:'',

  });

  // Dropdown states



  const states = {
    "West Bengal": ["Kolkata", "Asansol"],
    Bihar: ["Patna", "Gaya"],
    Jharkhand: ["Ranchi", "Jamshedpur"],
    Odisha: ["Bhubaneswar", "Cuttack"],
  };

  useEffect(() => {
    setCities(states[selectedState] || []);
  }, [selectedState]);

  // OTP handling (simple simulation)
  const [otpSent, setOtpSent] = useState({ mobile: false, aadhaar: false });
  const [otpVerified, setOtpVerified] = useState({ mobile: false, aadhaar: false });
  const [verifying, setVerifying] = useState({ mobile: false, aadhaar: false });

  const sendOtp = (target) => {
    setOtpSent((prev) => ({ ...prev, [target]: true }));
  };

  const verifyOtp = (target) => {
    setVerifying((prev) => ({ ...prev, [target]: true }));
    setTimeout(() => {
      setOtpVerified((prev) => ({ ...prev, [target]: true }));
      setVerifying((prev) => ({ ...prev, [target]: false }));
    }, 1500);
  };

  const steps = ["Personal Details", "Verification", "Secure Account"];

  const nextStep = () => setCurrentStep((prev) => Math.min(prev + 1, 2));
  const prevStep = () => setCurrentStep((prev) => Math.max(prev - 1, 0));

  const passwordRegex =
    /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;

  const handlePasswordChange = (e) => {
    const value = e.target.value;
    setPassword(value);

    if (!passwordRegex.test(value)) {
      setError(
        "Password must be at least 8 characters and include A-Z, a-z, 0-9, and a special character."
      );
    } else {
      setError("");
    }
  };

    const handlemOtpChange = (value, index) => {
    if (/^[0-9]?$/.test(value)) {
      const newOtp = [...motp];
      newOtp[index] = value;
      setMotp(newOtp);

      // Auto move focus
      if (value && index < motp.length - 1) {
        document.getElementById(`otp-${index + 1}`).focus();
      }
    }
  };

  const handlemOtpKeyDown = (e, index) => {
    if (e.key === "Backspace" && !motp[index] && index > 0) {
      document.getElementById(`otp-${index - 1}`).focus();
    }
  };

    const handleAdrOtpChange = (value, index) => {
    if (/^[0-9]?$/.test(value)) {
      const newOtp = [...adrotp];
      newOtp[index] = value;
      setAdrotp(newOtp);

      // Auto move focus
      if (value && index < adrotp.length - 1) {
        document.getElementById(`otp-${index + 1}`).focus();
      }
    }
  };

  const handleAdrOtpKeyDown = (e, index) => {
    if (e.key === "Backspace" && !adrotp[index] && index > 0) {
      document.getElementById(`otp-${index - 1}`).focus();
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("âœ… Account created successfully!");
  };

  return (
    <>
      <Header></Header>
      <div className="w-full bg-white flex justify-center ">
        <div className="bg-white p-8 rounded-xl shadow-2xl max-w-2xl w-full my-8">
          {/* Header */}
          <div className="text-center mb-4">
            <a href="/" className="flex items-center justify-center space-x-3">
              <HeartPulse className="h-12 w-12 text-blue-600" />
              <div>
                <h1 className="text-3xl font-bold text-gray-800">
                  Arogya Connect
                </h1>
                <p className="text-xs text-gray-500">Your Health, Connected</p>
              </div>
            </a>
          </div>

          {/* Title */}
          <h2 className="text-2xl font-bold text-gray-800 text-center">
            Create Your Health ID
          </h2>
          <p className="text-center text-gray-500 text-sm mt-2 mb-3">
            A single, secure ID for all your health records.
          </p>

          {/* Step Indicators */}
          <div className="flex items-center justify-center space-x-4 mb-3">
            {steps.map((_, i) => (
              <React.Fragment key={i}>
                <div
                  className={`w-10 h-10 rounded-full flex items-center justify-center font-bold ${
                    i < currentStep
                      ? "bg-green-500 text-white"
                      : i === currentStep
                      ? "bg-blue-600 text-white"
                      : "bg-gray-200 text-gray-500"
                  }`}
                >
                  {i < currentStep ? <Check className="h-5 w-5" /> : i + 1}
                </div>
                {i < steps.length - 1 && (
                  <div className="flex-1 h-1 bg-gray-200"></div>
                )}
              </React.Fragment>
            ))}
          </div>

          <form onSubmit={handleSubmit}>
            {/* Step 1 */}
            {currentStep === 0 && (
              <div>
                <h3 className="font-semibold text-lg mb-2 text-center">
                  Step 1: Personal Details
                </h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-sm mb-1">Full Name</label>
                    <input
                      type="text"
                      name="fullname"
                      className="w-full p-2 border border-gray-300 rounded-md"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm mb-1">Age</label>
                    <input
                      type="number"
                      name="age"
                      className="w-full p-2 border border-gray-300 rounded-md"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm mb-1">Gender</label>
                    <select className="w-full p-2 border border-gray-300 rounded-md bg-white">
                      <option>Male</option>
                      <option>Female</option>
                      <option>Other</option>
                    </select>
                  </div>
                  <div>
                    <label className="block text-sm mb-1">Email ID</label>
                    <input
                      type="email"
                      name="email"
                      className="w-full p-2 border border-gray-300 rounded-md"
                    />
                  </div>
                  <div className="md:col-span-2 grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                      <label className="block text-sm mb-1">Home State</label>
                      <select
                        value={selectedState}
                        onChange={(e) => setSelectedState(e.target.value)}
                        className="w-full p-2 border border-gray-300 rounded-md bg-white"
                      >
                        {Object.keys(states).map((state) => (
                          <option key={state} value={state}>
                            {state}
                          </option>
                        ))}
                      </select>
                    </div>
                    <div>
                      <label className="block text-sm mb-1">Home City</label>
                      <select className="w-full p-2 border border-gray-300 rounded-md bg-white">
                        {cities.map((city) => (
                          <option key={city}>{city}</option>
                        ))}
                      </select>
                    </div>
                  </div>
                  <div className="md:col-span-2">
                    <label className="block text-sm mb-1">
                      Address & Pin Code
                    </label>
                    <input
                      type="text"
                      name="Village"
                      placeholder="Village/Area"
                      className="w-full p-2 border border-gray-300 rounded-md"
                    />
                  </div>
                </div>
                <div className="mt-4 text-right">
                  <button
                    type="button"
                    onClick={nextStep}
                    className="bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700"
                  >
                    Next Step
                  </button>
                </div>
              </div>
            )}

            {/* Step 2 */}
            {currentStep === 1 && (
              <div>
                <h3 className="font-semibold text-lg mb-2 text-center">
                  Step 2: Verification
                </h3>
                <div className="space-y-6">
                  {/* Mobile OTP */}
                  <div>
                    <label className="block text-sm mb-1">Mobile Number</label>
                    <div className="flex space-x-2">
                      <input
                        type="number"
                        name="mobileNo"
                        className="w-full p-2 border border-gray-300 rounded-md"
                        required
                      />
                      <button
                        type="button"
                        onClick={() => sendOtp("mobile")}
                        className="bg-gray-200 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-300 text-sm"
                      >
                        {otpSent.mobile ? "Resend OTP" : "Send OTP"}
                      </button>
                    </div>
                    {otpSent.mobile && (
                      <div className="mt-2 flex space-x-2">
                        <div className="flex justify-center space-x-2">
                          {motp.map((digit, index) => (
                            <input
                              key={index}
                              id={`otp-${index}`}
                              type="text"
                              maxLength="1"
                              value={digit}
                              onChange={(e) => handlemOtpChange(e.target.value, index)}
                              onKeyDown={(e) => handlemOtpKeyDown(e, index)}
                              className="w-12 h-14 text-center text-2xl font-semibold border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                            />
                          ))}
                        </div>
                        <button
                          type="button"
                          onClick={(e) => verifyOtp("mobile")}
                          disabled={verifying.mobile}
                          className={`px-4 py-2 rounded-md text-sm font-semibold ${
                            otpVerified.mobile
                              ? "bg-green-500 text-white"
                              : "bg-teal-500 text-white hover:bg-teal-600"
                          }`}
                        >
                          {verifying.mobile
                            ? "Verifying..."
                            : otpVerified.mobile
                            ? "âœ“ Verified"
                            : "Verify"}
                        </button>
                      </div>
                    )}
                  </div>

                  {/* Aadhaar OTP */}
                  <div>
                    <label className="block text-sm mb-1">Aadhaar Number</label>
                    <div className="flex space-x-2">
                      <input
                        type="number"
                        className="w-full p-2 border border-gray-300 rounded-md"
                        required
                      />
                      <button
                        type="button"
                        onClick={() => sendOtp("aadhaar")}
                        className="bg-gray-200 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-300 text-sm"
                      >
                        {otpSent.aadhaar ? "Resend OTP" : "Send OTP"}
                      </button>
                    </div>
                    {otpSent.aadhaar && (
                      <div className="mt-2 flex gap-4 space-x-2">
                        <div className="flex justify-center space-x-2">
                          {adrotp.map((digit, index) => (
                            <input
                              key={index}
                              id={`otp-${index}`}
                              type="text"
                              maxLength="1"
                              value={digit}
                              onChange={(e) => handleAdrOtpChange(e.target.value, index)}
                              onKeyDown={(e) => handleAdrOtpKeyDown(e, index)}
                              className="w-12 h-14 text-center text-2xl font-semibold border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                            />
                          ))}
                        </div>
                        <button
                          type="button"
                          onClick={() => verifyOtp("aadhaar")}
                          disabled={verifying.aadhaar}
                          className={`px-4 py-2 rounded-md text-sm font-semibold ${
                            otpVerified.aadhaar
                              ? "bg-green-500 text-white"
                              : "bg-teal-500 text-white hover:bg-teal-600"
                          }`}
                        >
                          {verifying.aadhaar
                            ? "Verifying..."
                            : otpVerified.aadhaar
                            ? "âœ“ Verified"
                            : "Verify"}
                        </button>
                      </div>
                    )}
                  </div>
                </div>
                <div className="mt-8 flex justify-between">
                  <button
                    type="button"
                    onClick={prevStep}
                    className="bg-gray-300 text-gray-800 px-6 py-2 rounded-md hover:bg-gray-400"
                  >
                    Back
                  </button>
                  <button
                    type="button"
                    onClick={nextStep}
                    className="bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700"
                  >
                    Next Step
                  </button>
                </div>
              </div>
            )}

            {/* Step 3 */}
            {currentStep === 2 && (
              <div>
                <h3 className="font-semibold text-lg mb-4 text-center">
                  Step 3: Secure Your Account
                </h3>
                <div className="space-y-6">
                  <div>
                    <label className="block text-sm mb-1">
                      Create Password
                    </label>
                    <div className="relative">
                      <input
                        type={showPassword ? "text" : "password"}
                        value={password}
                        onChange={handlePasswordChange}
                        className={`w-full p-2 border rounded-md ${
                          error ? "border-red-500" : "border-gray-300"
                        }`}
                        required
                      />
                      <button
                        type="button"
                        onClick={() => setShowPassword(!showPassword)}
                        className="absolute inset-y-0 right-0 px-3 flex items-center text-gray-500"
                      >
                        {showPassword ? (
                          <EyeOff className="h-5 w-5" />
                        ) : (
                          <Eye className="h-5 w-5" />
                        )}
                      </button>
                    </div>
                    {error && (
                      <p className="text-red-500 text-sm mt-1">{error}</p>
                    )}
                  </div>

                  <div>
                    <label className="block text-sm mb-1">
                      Confirm Password
                    </label>
                    <input
                      type="password"
                      value={confirmPassword}
                      onChange={(e) => setConfirmPassword(e.target.value)}
                      className={`w-full p-2 border rounded-md ${
                        confirmPassword && confirmPassword !== password
                          ? "border-red-500"
                          : "border-gray-300"
                      }`}
                      required
                    />
                    {confirmPassword && confirmPassword !== password && (
                      <p className="text-red-500 text-sm mt-1">
                        Passwords do not match
                      </p>
                    )}
                  </div>
                </div>
                <div className="mt-6">
                  <label className="flex items-start">
                    <input
                      type="checkbox"
                      className="h-5 w-5 rounded border-gray-300 text-blue-600 focus:ring-blue-500 mt-0.5"
                      required
                    />
                    <span className="ml-3 text-sm text-gray-600 flex">
                      I agree to the{" "}
                      <p className="text-blue-600 hover:underline">
                        Terms of Service
                      </p>{" "}
                      and{" "}
                      <p className="text-blue-600 hover:underline">
                        Privacy Policy
                      </p>
                      .
                    </span>
                  </label>
                </div>
                <div className="mt-8 flex justify-between">
                  <button
                    type="button"
                    onClick={prevStep}
                    className="bg-gray-300 text-gray-800 px-6 py-2 rounded-md hover:bg-gray-400"
                  >
                    Back
                  </button>
                  <button
                    type="submit"
                    className="bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700"
                  >
                    Create Account
                  </button>
                </div>
              </div>
            )}
          </form>

          {/* Footer */}
          <div className="mt-6 pt-6 border-t text-center text-sm">
            <p className="text-gray-500">
              Already have an account?{" "}
              <Link
                to="/login"
                className="font-semibold text-blue-600 hover:underline"
              >
                Login Here
              </Link>
            </p>
          </div>
        </div>
      </div>
    </>
  );
};

export default RegisterCard;
