import React, { useState } from "react";
import { ArrowLeft, ScanLine } from "lucide-react";

const EncounterForm = () => {
  const [patientId, setPatientId] = useState("");
  const [selectedSymptoms, setSelectedSymptoms] = useState([]);
  const [vitals, setVitals] = useState({ temp: "", spo2: "", bp: "" });
  const [notes, setNotes] = useState("");
  const [doctor, setDoctor] = useState("Dr. Rajesh Kumar");

  const availableSymptoms = [
    "Fever",
    "Cough",
    "Headache",
    "Body Ache",
    "Weakness",
    "Stomach Pain",
  ];


  const toggleSymptom = (symptom) => {
    setSelectedSymptoms((prev) =>
      prev.includes(symptom)
        ? prev.filter((s) => s !== symptom)
        : [...prev, symptom]
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log({
      patientId,
      selectedSymptoms,
      vitals,
      notes,
      doctor,
    });
    alert("Encounter forwarded to doctor!");
  };

  return (
    <div id="encounter-view" className="page-view p-2">
      {/* Back button */}
      <button onC className="back-btn flex items-center text-sm font-semibold text-gray-600 hover:text-black mb-4">
        <ArrowLeft className="h-4 w-4 mr-2" /> Back to Dashboard
      </button>

      {/* Form */}
      <form
        id="encounter-form"
        onSubmit={handleSubmit}
        className="bg-white p-8 rounded-lg shadow max-w-2xl mx-auto"
      >
        <h2 className="text-xl font-bold text-gray-800 mb-6 border-b pb-4">
          Start New Encounter
        </h2>
          <div id="symptom-form-section">
            {/* Patient Info */}
            <div className="bg-gray-50 p-4 rounded-md mb-6 text-sm">
              <p>
                <strong>Patient:</strong>{" "}
                <span className="font-semibold">Amit Singh</span> (ID:{" "}
                <span>{patientId}</span>)
              </p>
              <p>
                <strong>Last Visit:</strong> 1 week ago
              </p>
            </div>

            {/* Symptoms */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Select Reported Symptoms
              </label>
              <div className="flex flex-wrap gap-3">
                {availableSymptoms.map((symptom) => (
                  <button
                    key={symptom}
                    type="button"
                    className={`symptom-btn border px-3 py-2 rounded-full text-sm font-medium ${
                      selectedSymptoms.includes(symptom)
                        ? "bg-teal-100 border-teal-500 text-teal-700"
                        : ""
                    }`}
                    onClick={() => toggleSymptom(symptom)}
                  >
                    {symptom}
                  </button>
                ))}
              </div>
              <input
                type="text"
                className="w-full p-2 border rounded-md mt-4 text-sm"
                placeholder="Other symptoms..."
              />
            </div>

            {/* Vitals */}
            <div className="mt-6 border-t pt-6">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Record Vitals
              </label>
              <div className="grid grid-cols-3 gap-4">
                <div>
                  <label className="text-xs">Temp (Â°F)</label>
                  <input
                    type="text"
                    className="w-full p-2 border rounded-md mt-1"
                    value={vitals.temp}
                    onChange={(e) =>
                      setVitals({ ...vitals, temp: e.target.value })
                    }
                    placeholder="e.g., 102.4"
                  />
                </div>
                <div>
                  <label className="text-xs">SpO2 (%)</label>
                  <input
                    type="text"
                    className="w-full p-2 border rounded-md mt-1"
                    value={vitals.spo2}
                    onChange={(e) =>
                      setVitals({ ...vitals, spo2: e.target.value })
                    }
                    placeholder="e.g., 96"
                  />
                </div>
                <div>
                  <label className="text-xs">BP (SYS/DIA)</label>
                  <input
                    type="text"
                    className="w-full p-2 border rounded-md mt-1"
                    value={vitals.bp}
                    onChange={(e) =>
                      setVitals({ ...vitals, bp: e.target.value })
                    }
                    placeholder="e.g., 120/80"
                  />
                </div>
              </div>
            </div>

            {/* Notes */}
            <div className="mt-6">
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Additional Notes
              </label>
              <textarea
                rows="3"
                className="w-full p-2 border rounded-md"
                placeholder="Add any other observations..."
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
              ></textarea>
            </div>

            {/* Forward */}
            <div className="mt-6">
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Forward To Doctor
              </label>
              <select
                className="w-full p-2 border rounded-md bg-white"
                value={doctor}
                onChange={(e) => setDoctor(e.target.value)}
              >
                <option>Dr. Rajesh Kumar</option>
                <option>Dr. Anjali Menon</option>
              </select>
            </div>

            {/* Submit */}
            <div className="mt-8 text-right">
              <button
                type="submit"
                className="bg-teal-600 text-white font-semibold px-6 py-2 rounded-md hover:bg-teal-700"
              >
                Forward to Doctor
              </button>
            </div>
          </div>

      </form>
    </div>
  );
};

export default EncounterForm;
