import React, { useState } from 'react';
import {
  UserHeart, LogOut, Calendar, Heart, Activity, Clock, CheckCircle, Pill,
  ArrowLeft, CalendarCheck, Thermometer, Stethoscope, Microscope,
  CalendarPlus, Check, AlertCircle, Download, Eye
} from 'lucide-react';

// --- Mock Data ---
const MOCK_APPOINTMENTS = {
  ongoing: [
    { id: 'APP-001', date: 'Sep 12, 2025', time: '2:30 PM', doctor: 'Dr. Rajesh Kumar', specialty: 'General Physician', diagnosis: 'Viral Fever', status: 'ongoing', statusText: 'Treatment Ongoing', statusColor: 'bg-orange-100 text-orange-800', borderColor: 'border-orange-500', symptoms: 'Fever, body aches, chest congestion.', medicines: [ { name: 'Paracetamol', dosage: '500mg', frequency: 'Twice daily', duration: '5 days', status: 'Active' }, { name: 'Cetirizine', dosage: '10mg', frequency: 'Once daily', duration: '3 days', status: 'Active' } ], labTests: [ { name: 'Complete Blood Count (CBC)', status: 'Completed', date: 'Sep 10, 2025', reportFile: 'cbc_AC12345_sep2025.pdf' }, { name: 'Chest X-Ray', status: 'Pending', date: 'Sep 13, 2025', reportFile: null } ], vitals: { temp: '102.4°F', bp: '124/82', pulse: '78 bpm', weight: '72 kg' } },
    { id: 'APP-002', date: 'Sep 10, 2025', time: '11:00 AM', doctor: 'Dr. Priya Patel', specialty: 'Cardiologist', diagnosis: 'Hypertension Follow-up', status: 'ongoing', statusText: 'Treatment Ongoing', statusColor: 'bg-blue-100 text-blue-800', borderColor: 'border-blue-500', symptoms: 'Regular blood pressure monitoring', medicines: [ { name: 'Amlodipine', dosage: '5mg', frequency: 'Once daily', duration: '30 days', status: 'Active' } ], labTests: [ { name: 'ECG', status: 'Completed', date: 'Sep 9, 2025', reportFile: 'ecg_AC12345_sep2025.pdf' } ], vitals: { temp: '98.6°F', bp: '130/85', pulse: '72 bpm', weight: '72 kg' } }
  ],
  pending: [
